// Prepare lead data for analysis
const lead = $json;

return {
  company_name: lead.name || lead.company || 'Unknown Company',
  website: lead.website || '',
  description: lead.description || lead.about || '',
  original_data: lead
};

// Combine all analysis results into final format
const companyAnalysis = $json.output;
const rfrncsAnalysis = $json.output;
const leadData = $json;

return {
  company_name: leadData.company_name,
  website: leadData.website,

  // Company Analysis Results
  company_overview: companyAnalysis.company_overview || '',
  products_services: Array.isArray(companyAnalysis.products_services) ? companyAnalysis.products_services.join('; ') : companyAnalysis.products_services || '',
  target_market: companyAnalysis.target_market || '',
  business_model: companyAnalysis.business_model || '',
  industry: companyAnalysis.industry || '',
  key_features: Array.isArray(companyAnalysis.key_features) ? companyAnalysis.key_features.join('; ') : companyAnalysis.key_features || '',
  competitive_advantage: companyAnalysis.competitive_advantage || '',

  // RFRNCS Fit Analysis
  fit_score: rfrncsAnalysis.fit_score || 0,
  needs_branding_services: rfrncsAnalysis.needs_branding_services || false,
  recommended_services: Array.isArray(rfrncsAnalysis.recommended_services) ? rfrncsAnalysis.recommended_services.join('; ') : rfrncsAnalysis.recommended_services || '',
  budget_potential: rfrncsAnalysis.budget_potential || '',
  urgency_level: rfrncsAnalysis.urgency_level || '',
  conversion_probability: rfrncsAnalysis.conversion_probability || '',
  reasoning: rfrncsAnalysis.reasoning || '',
  next_steps: rfrncsAnalysis.next_steps || '',

  analysis_date: new Date().toISOString()
};
